mapper.delete:
delete from t_deploy_dbscript_sync_detailsql
where deploydbscriptid = #{deploydbscriptid,jdbcType=VARCHAR}

AND   executestatus <> 1

mapper.select:
select COALESCE(max(subsqlseqno), 0)
from t_deploy_dbscript_sync_detailsql
where deploydbscriptid = #{deploydbscriptid,jdbcType=VARCHAR}

select count(1)
from t_deploy_dbscript_sync_detailsql
where deploydbscriptid = #{deploydbscriptid,jdbcType=VARCHAR}
